	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward lista(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		ConversionForm conversionForm = (ConversionForm)form;
		InvCnvConversionDAO conversionDAO = new InvCnvConversionDAO(getSessionHibernate(request));
		conversionForm.getConversionH().setCnvComentario("");
		request.getSession().setAttribute(CONV_MED_ID, conversionForm.getMedId());
		InvMedMedidaDAO medidaDAO = new InvMedMedidaDAO(getSessionHibernate(request));
		
		InvMedMedida medida = medidaDAO.findById(conversionForm.getMedId());
		conversionForm.setMedida(conversionForm.getMedId());
		request.setAttribute("medida", medida);
//		InvMedMedida medida = medidaDAO.findById(conversionForm.getInvMedMedida().getMedId());
		
		TableFacade tableFacade = new TableFacadeImpl(TABLA_ID, request);
		tableFacade.setItems(medida.getInvCnvConversions());
		//---- Genera los tipos de formas con que se podran exportar los datos
		tableFacade.setExportTypes(response, ExportType.CSV, ExportType.JEXCEL);//, ExportType.PDF);
		tableFacade.setStateAttr("restore");
		Limit limit = tableFacade.getLimit();
		if (limit.isExported()) {
        	//---- exporta la tabla
            export(tableFacade);
            return null; 
        } else {
        	//---- genera el html de la tabla para ser mostrada
            String html = html(tableFacade, request);
            request.setAttribute(Constantes.LISTA_KEY, html);
        }
        //----- Variables de configuracion
		request.setAttribute("form", conversionForm);
		request.setAttribute(Constantes.ACCION_KEY, "/conversion");
        request.setAttribute("filtro", "1");
		return mapping.findForward("lista");
		 
	}
	
	//---- Funcion que genera el html de la tabla del jmesa
	private String html(final TableFacade tableFacade, final HttpServletRequest request) {
		tableFacade.setColumnProperties("cnvNuevaMedida","cnvFactor","cnvNombreMedida",
				"cnvComentario","invMedMedida.medId");
		Table table = tableFacade.getTable();
		//---- Titulo de la tabla
		table.setCaptionKey("tbl.conversion.caption");
		
		Row row = table.getRow();
		Column nombreColumna = row.getColumn("cnvNuevaMedida");
		nombreColumna.setTitleKey("tbl.conversion.cnvNuevaMedida");
		
		nombreColumna = row.getColumn("cnvFactor");
		nombreColumna.setTitleKey("tbl.conversion.cnvFactor");
		
		nombreColumna = row.getColumn("cnvNombreMedida");
		nombreColumna.setTitleKey("tbl.conversion.cnvNombreMedida");
		
		nombreColumna = row.getColumn("cnvComentario");
		nombreColumna.setTitleKey("tbl.conversion.cnvComentario");
		
		nombreColumna = row.getColumn("invMedMedida.medId");
		nombreColumna.setTitleKey("tbl.conversion.eliminar");
		nombreColumna.getCellRenderer().setCellEditor(new CellEditor(){

			public Object getValue(Object item, String property, int rowcount) {
				Object value = new BasicCellEditor().getValue(item, property, rowcount);
				InvCnvConversion conversion = (InvCnvConversion)item;
				
				HtmlBuilder html = new HtmlBuilder();
				value = "Eliminar";
				//String link = tableFacade.getWebContext().getContextPath();
				html.a().style("cursor: pointer;").onclick("handlerDeleteButton("+ conversion.getCnvId()+ ");").append("class=\"linkEliminar\"").title(value.toString()).id("deleteButtonId").close();
				//html.append(value);
				html.aEnd();
								
				return html.toString();
			}
		});
		
		return tableFacade.render();
	}
	
	//---- Funcion que genera los exports, el formato que tendran
	 private void export(final TableFacade tableFacade) {
		 tableFacade.setColumnProperties("cnvNuevaMedida","cnvFactor","cnvNombreMedida",
					"cnvComentario");
			Table table = tableFacade.getTable();
			//---- Titulo de la tabla
			table.setCaptionKey("tbl.conversion.caption.x");
			
			Row row = table.getRow();
			Column nombreColumna = row.getColumn("cnvNuevaMedida");
			nombreColumna.setTitleKey("tbl.conversion.cnvNuevaMedida.x");
			
			nombreColumna = row.getColumn("cnvFactor");
			nombreColumna.setTitleKey("tbl.conversion.cnvFactor");
			
			nombreColumna = row.getColumn("cnvNombreMedida");
			nombreColumna.setTitleKey("tbl.conversion.cnvNombreMedida");
			
			nombreColumna = row.getColumn("cnvComentario");
			nombreColumna.setTitleKey("tbl.conversion.cnvComentario");
		
		tableFacade.render();
	}
	 
	public ActionForward guardar(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		ConversionForm conversionForm = (ConversionForm)form;
		InvCnvConversionDAO conversionDAO = new InvCnvConversionDAO(getSessionHibernate(request));
		Transaction tx = conversionDAO.getSession().beginTransaction();
		InvMedMedidaDAO medidaDAO = new InvMedMedidaDAO(getSessionHibernate(request));
		
		if(conversionForm.getCnvFactor() == 0.0){
			mensajes("errors.conversion.factorZero", conversionForm, request, response);
			return mapping.findForward("lista");
		}
		if(conversionForm.getCnvFactor() < 0.0){
			mensajes("errors.conversion.negativo", conversionForm, request, response);
			return mapping.findForward("lista");
		}
		
		try {
			conversionForm.getConversionH().setInvMedMedida(medidaDAO.findById(conversionForm.getMedId()));
			conversionDAO.save(conversionForm.getConversionH());
			tx.commit();
		} catch (Exception e) {
			// TODO: handle exception
			tx.rollback();
			e.printStackTrace();
		}finally{
			conversionDAO.getSession().flush();
			conversionDAO.getSession().clear();
			
		}
		return lista(mapping,form,request,response);
	}
	
	public ActionForward salvar(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		ConversionForm conversionForm = (ConversionForm)form;
		InvCnvConversionDAO conversionDAO = new InvCnvConversionDAO(getSessionHibernate(request));
		Transaction tx = conversionDAO.getSession().beginTransaction();
		try {
			conversionDAO.merge(conversionForm.getConversionH());
			tx.commit();
		} catch (Exception e) {
			// TODO: handle exception
			tx.rollback();
			e.printStackTrace();
		}finally{
			conversionDAO.getSession().flush();
			conversionDAO.getSession().clear();
			
		}
		return lista(mapping,form,request,response);
	}

	public ActionForward eliminar(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		ConversionForm conversionForm = (ConversionForm)form;
		InvCnvConversionDAO  conversionDAO = new InvCnvConversionDAO(getSessionHibernate(request));
		Transaction tx = conversionDAO.getSession().beginTransaction();
		try {
			conversionForm.setConversionH(conversionDAO.findById(conversionForm.getCnvId()));
			conversionDAO.delete(conversionForm.getConversionH());
			tx.commit();
		} catch (Exception e) {
			// TODO: handle exception
			tx.rollback();
			e.printStackTrace();
		}finally{
			conversionDAO.getSession().flush();
			conversionDAO.getSession().clear();
			
		}
		return lista(mapping,form,request,response);
	}
	
