			mxs.setAudFechaCreacion(new Date());
			mxs.setAudFechaModificacion(new Date());
			mxs.setAudUsuarioCreacion(USUARIO);
			mxs.setAudUsuarioModificacion(USUARIO);
			mxsDao.save(mxs);
			
			Transaction tx1 = segurosDAO.getSession().beginTransaction();
