	//---- metodo que genera el html de la tabla del jmesa
	private String html(final TableFacade tableFacade, final HttpServletRequest request) {
		tableFacade.setColumnProperties("domNombre", "domDescripcion", "domId");
		Table table = tableFacade.getTable();
		//---- Titulo de la tabla
		table.setCaptionKey("tbl.dom.caption");
		
		Row row = table.getRow();
		Column nombreColumna = row.getColumn("domNombre");
		nombreColumna.setTitleKey("tbl.dom.domNombre");
		
		nombreColumna = row.getColumn("domDescripcion");
		nombreColumna.setTitleKey("tbl.dom.domDescripcion");
		
		nombreColumna = row.getColumn("domId");
		nombreColumna.setTitleKey("tbl.dom.acciones");
		nombreColumna.getCellRenderer().setCellEditor(new CellEditor(){

			public Object getValue(Object item, String property, int rowcount) {
				Object value = new BasicCellEditor().getValue(item, property, rowcount);
				CtaDomDomicilio tipoDomicilio = (CtaDomDomicilio)item;
				HtmlBuilder html = new HtmlBuilder();
				value = "Modificar";
				String link = tableFacade.getWebContext().getContextPath();
				link += "/asociados/tipoDomicilio.do?domId="+tipoDomicilio.getDomId()+"&domNombre="
				+tipoDomicilio.getDomNombre()+"&domDescripcion="+ tipoDomicilio.getDomDescripcion()+
				"&accion=cargarDatos&mdf=true";
				html.a().href().quote().append(link).quote().append("class=\"linkEditar\"").title(value.toString()).close();
				//html.append(value);
				html.aEnd();				
				return html.toString();		
			}
		});
		
		return tableFacade.render();
	}
	
	//---- metodo que genera los exports, el formato que tendran
	 private void export(final TableFacade tableFacade) {
		 tableFacade.setColumnProperties("domNombre", "domDescripcion");
			Table table = tableFacade.getTable();;
		
			//---- Titulo de la tabla
			table.setCaptionKey("tbl.dom.caption");
			
			Row row = table.getRow();
			Column nombreColumna = row.getColumn("domNombre");
			nombreColumna.setTitleKey("tbl.dom.etrNombre");
			
			nombreColumna = row.getColumn("domDescripcion");
			nombreColumna.setTitleKey("tbl.dom.domDescripcion");
			
		tableFacade.render();
	}
	
	 public ActionForward guardar(ActionMapping mapping, ActionForm form,
				HttpServletRequest request, HttpServletResponse response) {
			TipoDomicilioForm tipoDomicilioForm = (TipoDomicilioForm)form;
			CtaDomDomicilioDAO tipoDomicilioDAO = new CtaDomDomicilioDAO(getSessionHibernate(request));
			Transaction tx = tipoDomicilioDAO.getSession().beginTransaction();
			try{
				if(!tipoDomicilioForm.isMdf()){//ingresando un nuevo registro
					if(tipoDomicilioDAO.findByDomNombre(tipoDomicilioForm.getDomNombre()).isEmpty()){
						tipoDomicilioForm.getDomicilioH().setDomId(tipoDomicilioDAO.nextId());
						tipoDomicilioDAO.save(tipoDomicilioForm.getDomicilioH());
					}else{
						mensajes("errors.domNombreRepetido",request);
						return lista(mapping, form, request, response);
					}
				}else{//modificando un registro
					if(tipoDomicilioDAO.findByUpdatedName(tipoDomicilioForm.getDomId(),tipoDomicilioForm.getDomNombre()).isEmpty()){
						tipoDomicilioDAO.merge(tipoDomicilioForm.getDomicilioH());
					}else{
						mensajes("errors.domNombreRepetido",request);
						return lista(mapping, form, request, response);
					}
				}
				tx.commit();
			}catch(Exception e){
				tx.rollback();
				e.printStackTrace();
			}finally{
				tipoDomicilioDAO.getSession().flush();
				tipoDomicilioDAO.getSession().clear();
				
			}
			return lista(mapping,new TipoDomicilioForm(),request,response);
		} 
	 
	 public ActionForward cargarDatos(ActionMapping mapping, ActionForm form,
				HttpServletRequest request, HttpServletResponse response) {
		 return lista(mapping, form, request, response);
	 }
	 
		public ActionForward redirectInvalidData(ActionMapping mapping, ActionForm form,
				HttpServletRequest request, HttpServletResponse response){
			ActionErrors errors = (ActionErrors)request.getSession().getAttribute(Constantes.ERRORS);
			saveMessages(request, errors);
			return lista(mapping, form, request, response);
		}
		
		public void mensajes(String msg, HttpServletRequest request){
			ActionErrors errors = new ActionErrors();
	        errors.add(ActionMessages.GLOBAL_MESSAGE,new ActionMessage(msg));
	        saveMessages(request, errors);
		}
		
	
