	//---- metodo que genera el html de la tabla del jmesa
	private String html(final TableFacade tableFacade, final HttpServletRequest request) {
		tableFacade.setColumnProperties("ttrNombre", "ttrDescripcion", "ttrId");
		Table table = tableFacade.getTable();
		//---- Titulo de la tabla
		table.setCaptionKey("tbl.ttr.caption");
		
		Row row = table.getRow();
		Column nombreColumna = row.getColumn("ttrNombre");
		nombreColumna.setTitleKey("tbl.ttr.ttrNombre");
		
		nombreColumna = row.getColumn("ttrDescripcion");
		nombreColumna.setTitleKey("tbl.ttr.ttrDescripcion");
		
		nombreColumna = row.getColumn("ttrId");
		nombreColumna.setTitleKey("tbl.ttr.acciones");
		nombreColumna.getCellRenderer().setCellEditor(new CellEditor(){

			public Object getValue(Object item, String property, int rowcount) {
				Object value = new BasicCellEditor().getValue(item, property, rowcount);
				CtaTtrTipoTransaccion tipoTransaccion = (CtaTtrTipoTransaccion)item;
				HtmlBuilder html = new HtmlBuilder();
				value = "Editar";
				String link = tableFacade.getWebContext().getContextPath();
				link += "/asociados/tipoTransaccion.do?ttrId="+tipoTransaccion.getTtrId()+"&ttrNombre="
				+tipoTransaccion.getTtrNombre()+"&ttrDescripcion="+ tipoTransaccion.getTtrDescripcion()+
				"&ttrUso="+tipoTransaccion.getTtrUso()+"&accion=cargarDatos&mdf=true";
				html.a().href().quote().append(link).quote().append("class=\"linkEditar\"").title(value.toString()).close();
			//	html.a().href().quote().append(link).quote().close();
			//	html.append(value);
				html.aEnd();				
				return html.toString();		
			}
		});
		
		return tableFacade.render();
	}
	
	//---- metodo que genera los exports, el formato que tendran
	 private void export(final TableFacade tableFacade) {
		 tableFacade.setColumnProperties("ttrNombre", "ttrDescripcion");
			Table table = tableFacade.getTable();;
			//---- Titulo de la tabla
			table.setCaptionKey("tbl.conversion.caption");
		
			//---- Titulo de la tabla
			table.setCaptionKey("tbl.ttr.caption");
			
			Row row = table.getRow();
			Column nombreColumna = row.getColumn("ttrNombre");
			nombreColumna.setTitleKey("tbl.ttr.ttrNombre");
			
			nombreColumna = row.getColumn("ttrDescripcion");
			nombreColumna.setTitleKey("tbl.ttr.ttrDescripcion");
			
		tableFacade.render();
	}
	
	 public ActionForward guardar(ActionMapping mapping, ActionForm form,
				HttpServletRequest request, HttpServletResponse response) {
			TipoTransaccionForm tipoTransaccionForm = (TipoTransaccionForm)form;
			CtaTtrTipoTransaccionDAO tipoTransaccionDAO = new CtaTtrTipoTransaccionDAO(getSessionHibernate(request));
			Transaction tx = tipoTransaccionDAO.getSession().beginTransaction();
			try{
				if(!tipoTransaccionForm.isMdf()){//ingresando un nuevo registro
					if(tipoTransaccionDAO.findByTtrNombre(tipoTransaccionForm.getTipoTransaccionH().getTtrNombre()).isEmpty()){
						tipoTransaccionForm.getTipoTransaccionH().setTtrId(tipoTransaccionDAO.nextId());
						tipoTransaccionDAO.save(tipoTransaccionForm.getTipoTransaccionH());
					}else{
						mensajes("errors.ttrNombreRepetido",request);
						return lista(mapping, form, request, response);
					}
				}else{//modificando un registro
					if(tipoTransaccionDAO.findByUpdatedName(tipoTransaccionForm.getTtrId(),tipoTransaccionForm.getTtrNombre()).isEmpty()){
						tipoTransaccionDAO.merge(tipoTransaccionForm.getTipoTransaccionH());
					}else{
						mensajes("errors.ttrNombreRepetido",request);
						return lista(mapping, form, request, response);
					}
				}
				tx.commit();
			}catch(Exception e){
				tx.rollback();
				e.printStackTrace();
			}finally{
				tipoTransaccionDAO.getSession().flush();
				tipoTransaccionDAO.getSession().clear();
				
			}
			return lista(mapping,new TipoTransaccionForm(),request,response);
		} 
	 
	 public ActionForward cargarDatos(ActionMapping mapping, ActionForm form,
				HttpServletRequest request, HttpServletResponse response) {
		 return lista(mapping, form, request, response);
	 }
	 
		public ActionForward redirectInvalidData(ActionMapping mapping, ActionForm form,
				HttpServletRequest request, HttpServletResponse response){
			ActionErrors errors = (ActionErrors)request.getSession().getAttribute(Constantes.ERRORS);
			saveMessages(request, errors);
			return lista(mapping, form, request, response);
		}
		
		public void mensajes(String msg, HttpServletRequest request){
			ActionErrors errors = new ActionErrors();
	        errors.add(ActionMessages.GLOBAL_MESSAGE,new ActionMessage(msg));
	        saveMessages(request, errors);
		}
		
	
