					seguros.setSegSaldoActual(saldoDespuesDeCambio + cuotasAdelAtras);
					
					String parametros = "1;4;"+seguros.getCtaTisTipoSeguro().getTisId()+";"+ttrACap.getTtrId()+";0;-1";
					PartidaAutomatica partidaAutomatica = new PartidaAutomatica();
