SELECT ((IF(asc1.asc_pago_ingreso = 'S', 0.0,  (SELECT par.par_valor_number  FROM ctr_par_parametros par  where par.par_nombre = 'VALOR_INSCRIPCION') )) + (SELECT  SUM(IF(cah.`cah_cuota` IS NULL,0.0,cah.`cah_cuota`)) AS cuota FROM    `cta_asc_asociado` asoc INNER JOIN `cta_cas_cuenta_asociado` cas   ON asoc.`ASC_ID` = cas.`ASC_ID`  INNER JOIN `cta_cah_cuenta_ahorro` cah ON cas.`cah_id` = cah.`cah_id` WHERE    cas.`EST_ID`=9 AND cah.`cah_id` LIKE 'A%' AND   IF(asc1.`ASC_PAGARA_PADRE`<> 'Y',asoc.`ASC_ID`=asc1.`ASC_ID`, (asoc.`ASC_ID`=asc1.`ASC_ID` OR asoc.`ASC_ID` IN (SELECT a.`ASC_ID` FROM cta_asc_asociado a    WHERE a.`ASC_ASOCIADO_PADRE`=asc1.`ASC_ID` AND a.`ASC_PAGARA_PADRE`='S'))))/2.0 + (SELECT SUM(IF(cah.`cah_cuota` IS NULL,0.0,cah.`cah_cuota`)) AS cuota FROM  `cta_asc_asociado` asoc    INNER JOIN `cta_cas_cuenta_asociado` cas ON asoc.`ASC_ID` = cas.`ASC_ID` INNER JOIN `cta_cah_cuenta_ahorro` cah  ON cas.`cah_id` = cah.`cah_id` WHERE    cas.`EST_ID`=9 AND   cah.`cah_id` LIKE 'B%' AND IF(asc1.`ASC_PAGARA_PADRE`<> 'Y',asoc.`ASC_ID`=asc1.`ASC_ID`,  (asoc.`ASC_ID`=asc1.`ASC_ID` OR asoc.`ASC_ID`  IN (SELECT a.`ASC_ID` FROM cta_asc_asociado a      WHERE a.`ASC_ASOCIADO_PADRE`=asc1.`ASC_ID` AND a.`ASC_PAGARA_PADRE`='S'))))/2.0 + IF(    (SELECT    SUM(IF(pre.`pre_cuota` IS NULL,0.0,pre.`pre_cuota`)) AS cuota     FROM      `cta_asc_asociado` asoc   INNER JOIN `cta_cas_cuenta_asociado` cas     ON asoc.`ASC_ID` = cas.`ASC_ID`    INNER JOIN `cta_pre_prestamo` pre ON cas.`pre_id` = pre.`pre_id` WHERE    cas.`EST_ID`=13 AND pre.`pre_saldo_actual_t` > 0 AND IF(asc1.`ASC_PAGARA_PADRE`<> 'Y',asoc.`ASC_ID`=asc1.`ASC_ID`,  (asoc.`ASC_ID`=asc1.`ASC_ID` OR asoc.`ASC_ID`  IN (SELECT a.`ASC_ID` FROM cta_asc_asociado a      WHERE a.`ASC_ASOCIADO_PADRE`=asc1.`ASC_ID` AND a.`ASC_PAGARA_PADRE`='S')))) is null,0.0, (SELECT    SUM(IF(pre.`pre_cuota` IS NULL,0.0,pre.`pre_cuota`)) AS cuota FROM  `cta_asc_asociado` asoc   INNER JOIN `cta_cas_cuenta_asociado` cas ON asoc.`ASC_ID` = cas.`ASC_ID` INNER JOIN `cta_pre_prestamo` pre ON cas.`pre_id` = pre.`pre_id` WHERE    cas.`EST_ID`=13 AND pre.`pre_saldo_actual_t` > 0.0 AND IF(asc1.`ASC_PAGARA_PADRE`<> 'Y',asoc.`ASC_ID`=asc1.`ASC_ID`,  (asoc.`ASC_ID`=asc1.`ASC_ID` OR asoc.`ASC_ID`  IN (SELECT a.`ASC_ID` FROM cta_asc_asociado a      WHERE a.`ASC_ASOCIADO_PADRE`=asc1.`ASC_ID` AND a.`ASC_PAGARA_PADRE`='S')))) )/2.0 +  if((select seg.`seg_saldo_actual`        FROM         `cta_asc_asociado` asoc  INNER JOIN `cta_cas_cuenta_asociado` cas         ON asoc.`ASC_ID` = cas.`ASC_ID`         INNER JOIN `cta_seg_seguros` seg  ON cas.`seg_id` = seg.`seg_id`      WHERE          cas.`EST_ID`=11 AND   IF(asc1.`ASC_PAGARA_PADRE`<> 'Y',asoc.`ASC_ID`=asc1.`ASC_ID`,    (asoc.`ASC_ID`=asc1.`ASC_ID` OR asoc.`ASC_ID`    IN (SELECT a.`ASC_ID` FROM cta_asc_asociado a        WHERE a.`ASC_ASOCIADO_PADRE`=asc1.`ASC_ID` AND a.`ASC_PAGARA_PADRE`='S'))))> 0,( if((SELECT SUM(IF(seg.`seg_cuota` IS NULL,0.0,seg.`seg_cuota`)) AS cuota FROM  `cta_asc_asociado` asoc  INNER JOIN `cta_cas_cuenta_asociado` cas ON asoc.`ASC_ID` = cas.`ASC_ID` INNER JOIN `cta_seg_seguros` seg  ON cas.`seg_id` = seg.`seg_id` WHERE    cas.`EST_ID`=11 AND IF(asc1.`ASC_PAGARA_PADRE`<> 'Y',asoc.`ASC_ID`=asc1.`ASC_ID`,  (asoc.`ASC_ID`=asc1.`ASC_ID` OR asoc.`ASC_ID`  IN (SELECT a.`ASC_ID` FROM cta_asc_asociado a      WHERE a.`ASC_ASOCIADO_PADRE`=asc1.`ASC_ID` AND a.`ASC_PAGARA_PADRE`='S')))) is null, 0.0, (SELECT SUM(IF(seg.`seg_cuota` IS NULL,0.0,seg.`seg_cuota`)) AS cuota     FROM      `cta_asc_asociado` asoc  INNER JOIN `cta_cas_cuenta_asociado` cas     ON asoc.`ASC_ID` = cas.`ASC_ID`    INNER JOIN `cta_seg_seguros` seg  ON cas.`seg_id` = seg.`seg_id` WHERE    cas.`EST_ID`=11 AND IF(asc1.`ASC_PAGARA_PADRE`<> 'Y',asoc.`ASC_ID`=asc1.`ASC_ID`,  (asoc.`ASC_ID`=asc1.`ASC_ID` OR asoc.`ASC_ID`  IN (SELECT a.`ASC_ID` FROM cta_asc_asociado a      WHERE a.`ASC_ASOCIADO_PADRE`=asc1.`ASC_ID` AND a.`ASC_PAGARA_PADRE`='S')))))/2.0),0.0)) as TOTAL 
FROM `sec_per_persona` per 
INNER JOIN `cta_asc_asociado` asc1 
ON per.`PER_ID` = asc1.`PER_ID` 
WHERE  asc1.`ASC_ID`= '200901140000'